tionPath        progress:(CFSFileTransferProgress)progress      completion:(CFSFileTransferCompletion)completion{    [_restAdapter downloadFile:self                            to:localDestinationPath                      progress:progress                    completion:completion];}- (void)versionsWIthStartVersion:(NSNumber *)startVersion                  endVersion:(NSNumber *)endVersion                       limit:(NSNumber *)limit              withCompletion:(void (^)(NSArray *items, CFSError *error))completion{    [_restAdapter getVersionsOfFile:self.path                       startVersion:startVersion                         endVersion:endVersion                              limit:limit                         completion:^(NSArray *items, CFSError *error) {        completion(items, error);    }];}- (void)readWithCompletion:(void (^)(NSInputStream *))completion{    [_restAdapter inputStreamOfFile:self completion:completion];}- (BOOL)setMime:(NSString *)newMime{    NSMutableDictionary *meta = [[NSMutableDictionary alloc]init];    meta[CFSresponseMimeKey] = newMime;    meta[CFSResponseVersionKey] = [@(self.version) stringValue];    CFSError *error;    NSDictionary *newMeta = [_restAdapter alterMetaDataSyncWithPath:self.path meta:meta type:self.type error:&error];    BOOL success = NO;    if (!error) {        _mime = newMeta[CFSresponseMimeKey];        success = ([_mime isEqualToString:newMime]);    }        return success;}- (void)downloadUrlWithCompletion:(void (^)(NSString *))completion{    [_restAdapter downloadUrlOfFile:self completion:completion];}- (BOOL)changeAttributes:(NSDictionary *)values ifConflict:(VersionExists)ifConflict{    return [super changeAttributes:values ifConflict:ifConflict];}- (BOOL)setApplicationData:(NSDictionary *)applicationData{    return [super setApplicationData:applicationData];}- (BOOL)setName:(NSString *)name{    return [super setName:name];}- (void)setItemAttributes:(NSDictionary *)values{    _size = [values[CFSresponseSizeKey] longLongValue];    _mime = values[CFSresponseMimeKey];    _extension = values[CFSresponseExtensionKey];    self.name = values[CFSResponseNameKey];    self.dateContentLastModified = [NSDate dateWithTimeIntervalSince1970:[values[CFSResponseDateContentLastModifiedKey] doubleValue]];    self.dateCreated = [NSDate dateWithTimeIntervalSince1970:[values[CFSResponseDateCreatedKey] doubleValue]];    self.version = [values[CFSResponseVersionKey] integerValue];    self.applicationData = values[CFSResponseApplicationDataKey];    self.dateMetaLastModified = [NSDate dateWithTimeIntervalSince1970:[values[CFSResponseDateMetaLastModifiedKey] doubleValue]];}@end